import {Component, OnInit} from '@angular/core';
import {ActivatedRoute} from "@angular/router";
import {LendingService} from "../../../service/lending.service";

import * as pdfMake from "pdfmake/build/pdfmake";
import * as pdfFonts from "pdfmake/build/vfs_fonts";

(pdfMake as any).vfs = pdfFonts.pdfMake.vfs;

@Component({
  selector: 'app-lending-detail',
  templateUrl: './lending-detail.component.html',
  styleUrls: ['./lending-detail.component.css']
})
export class LendingDetailComponent implements OnInit {
  lending: any;
  green='green';



  constructor(
    private activatedRoute: ActivatedRoute,
    private lendingService: LendingService,
  ) {
  }

  ngOnInit(): void {
    const id = String(this.activatedRoute.snapshot.paramMap.get('id'));
    this.lendingService.findById(id).subscribe(
      (data) => {
        this.lending = data;
        console.log(this.lending)
      }
    )
  }

  lendingPDF() {
    var body = [["Order", "ID", "ISBN", "Title"]];
    for (let i = 0; i < this.lending.bookDTOList.length; i++) {
      var bodyTemp = [];
      bodyTemp.push(i + 1)
      bodyTemp.push(this.lending.bookDTOList[i].id)
      bodyTemp.push(this.lending.bookDTOList[i].isbn)
      bodyTemp.push(this.lending.bookDTOList[i].title)
      body.push(bodyTemp)
    }
    var docDefinition = {
      margin: [0, 0, 0, 0],
      pageSize: 'A5',
      pageOrientation: 'landscape',
      info: {
        title: 'Lending: ' + this.lending.account.fullname + "- " + this.lending.loanDate
      },
      content: [
        {
          alignment: 'justify',
          columns: [
            {
              text: 'PHIẾU MƯỢN SÁCH',
              bold: true,
              alignment: 'center',
              fontSize: 15
            },
            {
              image: 'data:image/png;base64,',
              width: 40
            }
          ]
        },
        {
          text: 'Borrower ID:' + this.lending.account.id,
          fontSize: 8
        },
        {
          text: 'Borrower Name:' + this.lending.account.fullname,
          fontSize: 8
        },
        {
          text: 'Loan Date:' + this.lending.loanDate,
          fontSize: 8
        },
        {
          text: 'List of Books',
          fontSize: 8,
          bold: true
        },
        {
          table: {
            widths: [50, 50, 100, '*'],
            body: body
          },
          fontSize: 8,
          alignment: "center"
        },
        {
          alignment: 'justify',
          columns: [
            {},
            {},
            {
              text: 'Signature Confirms',
              fontSize: 8,
              bold: true,
              alignment: "center",
              margin: [0, 10, 0, 0],
            }
          ]
        },
        {
          alignment: 'justify',
          columns: [
            {},
            {},
            {
              text: this.lending.account.fullname,
              fontSize: 8,
              bold: true,
              alignment: "center",
              margin: [0, 50, 0, 0],
            }
          ]
        }
      ]
    }
    // @ts-ignore
    pdfMake.createPdf(docDefinition).open();
  }

}
